import Vue from 'vue';
import VueRouter from 'vue-router';
import HomeView from '../views/manager/HomeView.vue'; // 确保路径正确
import LoginView from '@/views/LoginView.vue';
import RegisterView from '@/views/RegisterView.vue';
import ManagerView from '@/views/ManagerView.vue';
import UserView from '@/views/manager/UserView.vue';

Vue.use(VueRouter);

const routes=[
  {
    path: '/',
    name: 'Manager',
    component: ManagerView,
    redirect:'home',
    children:[
      {path:'home',name:'HomeView',component:HomeView},
      {path:'user',name:'UserView',component:UserView},
    ]
  },
  {
    path: '/login',
    name: 'LoginView',
    component: LoginView,
  },
  {
    path: '/register',
    name: 'RegisterView',
    component: RegisterView,
  },
]
const router = new VueRouter({
  mode:'history',
  base:process.env.BASE_URL,
  routes
})

router.beforeEach((to,from,next)=>{
  let adminPaths = ['/user']
  let user = JSON.parse(localStorage.getItem('honey-user') || '{}')

  if (user.role !== '管理员' && adminPaths.includes(to.path)) {
    // 如果当前登录的用户不是管理员，然后当前的到达的路径是管理员才有权限访问的路径，那这个时候我就让用户去到一个没有权限的页面，不让他访问实际的页面
    next('/403')
  } else {
    next()
  }
})
export default router
